export default (state, action) => {
    switch(action.type) {
      case 'DELETE_FOOD':
        return {
          ...state,
          foods: state.foods.filter(foods => foods.id !== action.payload)
        }
      case 'ADD_FOOD':
        return {
          ...state,
          foods: [action.payload, ...state.foods]
        }
      default:
        return state;
    }
  }
